mapper.select:
SELECT o.business_code, o.order_num, p.product_po, p.product_status, o.item_code, o.item_nm, o.delivery_date, o.customer_code, o.customer_nm, o.request_num, o.sap_code, o.serial_no, o.comment, o.land, o.manager_nm, o.unit_price, o.menge, o.total_price, o.success_status,
ifnull(( SELECT SUM(ifnull(delivery_order_menge,0)) FROM delivery_order_tbl WHERE del_yn = 'N' AND order_num = o.order_num AND item_code = o.item_code GROUP BY item_code ),0) as order_stock_menge,
ifnull(( SELECT SUM(ifnull(warehousing_qty,0)) FROM warehouse_tbl WHERE 1 = 1 AND (business_code = 'MD1243' OR business_code = 'MD1282') AND item_code = o.item_code GROUP BY item_code ),0) as stock_menge,
o.creator, o.updater, o.date_created, o.date_updated FROM order_manager_tbl AS o LEFT JOIN product_tbl AS p ON o.order_num = p.order_num
WHERE 1 = 1
AND o.order_num = #{order_num}
AND o.request_num = #{request_num}
AND date_format(o.delivery_date, '%Y-%m-%d') BETWEEN left(#{delivery_date}, 10) AND right(#{delivery_date}, 10)
AND o.manager_nm = #{manager_nm}
AND o.customer_nm = #{customer_nm}
AND o.request_num = #{request_num}
AND o.item_code = #{item_code}
AND o.item_nm = #{item_nm}
AND p.product_po IS NULL
AND p.product_po IS NOT NULL AND p.product_status != 'y'
AND p.product_po IS NOT NULL AND p.product_status = 'y'
AND (ifnull(menge,0) - ifnull(( SELECT SUM(ifnull(delivery_order_menge,0)) FROM delivery_order_tbl WHERE del_yn = 'N' AND order_num = o.order_num AND item_code = o.item_code GROUP BY item_code ),0)) > 0
GROUP BY o.order_num, o.item_code
ORDER BY o.order_num, o.item_rec_id, o.date_created ASC

SELECT o.business_code, o.order_num, p.product_po, p.product_status, o.item_code, o.item_nm, o.delivery_date, o.customer_code, o.customer_nm, o.request_num, o.sap_code, o.serial_no, o.comment, o.land, o.manager_nm, o.unit_price, o.menge, o.total_price, o.success_status,
ifnull(( SELECT SUM(ifnull(delivery_order_menge,0)) FROM delivery_order_tbl WHERE del_yn = 'N' AND order_num = o.order_num AND item_code = o.item_code GROUP BY item_code ),0) as order_stock_menge,
ifnull(( SELECT SUM(ifnull(warehousing_qty,0)) FROM warehouse_tbl WHERE 1 = 1 AND (business_code = 'MD1243' OR business_code = 'MD1282') AND item_code = o.item_code GROUP BY item_code ),0) as stock_menge,
o.creator, o.updater, o.date_created, o.date_updated FROM order_manager_tbl AS o LEFT JOIN product_tbl AS p ON o.order_num = p.order_num
WHERE 1 = 1
AND o.order_num = #{order_num}
AND o.request_num = #{request_num}
AND date_format(o.delivery_date, '%Y-%m-%d') BETWEEN left(#{delivery_date}, 10) AND right(#{delivery_date}, 10)
AND o.manager_nm = #{manager_nm}
AND o.customer_nm = #{customer_nm}
AND o.request_num = #{request_num}
AND o.item_code = #{item_code}
AND o.item_nm = #{item_nm}
AND p.product_po IS NULL
AND p.product_po IS NOT NULL AND p.product_status != 'y'
AND p.product_po IS NOT NULL AND p.product_status = 'y'
GROUP BY o.order_num, o.item_code
ORDER BY o.order_num, o.item_rec_id, o.date_created ASC

SELECT *
FROM (
SELECT o.business_code, o.order_num, o.item_code, o.item_nm, o.delivery_date, o.customer_code, o.customer_nm, o.request_num, o.sap_code, o.serial_no, o.comment, o.land, o.manager_nm, o.unit_price, o.menge, o.total_price, o.success_status, SUM(ifnull(p.delivery_menge,0)) AS delivery_order_menge,
CASE WHEN (o.menge = SUM(ifnull(p.delivery_menge,0)) ) THEN 'A' ELSE 'B' END AS process_status,
o.creator, o.updater, o.date_created, o.date_updated FROM order_manager_tbl AS o LEFT JOIN ( SELECT dt.delivery_num, dt.delivery_menge, dot.order_num, dt.item_code
FROM delivery_tbl AS dt LEFT JOIN delivery_order_tbl AS dot ON dt.delivery_num = dot.delivery_num AND dt.item_code = dot.item_code ) AS p ON o.order_num = p.order_num AND o.item_code = p.item_code
WHERE 1 = 1
AND o.order_num = #{order_num}
AND o.request_num = #{request_num}
AND date_format(o.delivery_date, '%Y-%m-%d') BETWEEN left(#{delivery_date}, 10) AND right(#{delivery_date}, 10)
AND o.manager_nm = #{manager_nm}
AND o.customer_nm = #{customer_nm}
AND o.request_num = #{request_num}
AND o.item_code = #{item_code}
AND o.item_nm = #{item_nm}
GROUP BY o.order_num, o.item_code
ORDER BY o.order_num, o.item_rec_id, o.date_created ASC
) AS pp WHERE 1 = 1
AND process_status = 'A'
AND process_status = 'B'

mapper.delete:
DELETE FROM order_manager_tbl
WHERE 1 = 1

	AND order_num = #{order_num}

DELETE FROM order_manager_tbl
WHERE 1 = 1

	AND order_num = #{order_num}


	AND item_code = #{item_code}


	AND request_num = #{request_num}

mapper.select:
SELECT o.business_code, o.order_num, o.item_code, o.item_nm, o.delivery_date, o.customer_code, o.customer_nm, o.land, o.unit_price, o.menge, o.total_price, o.success_status,
ifnull(( SELECT SUM(ifnull(delivery_order_menge,0)) FROM delivery_order_tbl WHERE del_yn = 'N' AND order_num = o.order_num AND item_code = o.item_code GROUP BY item_code ),0) as order_stock_menge,
ifnull(( SELECT SUM(ifnull(warehousing_qty,0)) FROM warehouse_tbl WHERE 1 = 1 AND (business_code = 'MD1243' OR business_code = 'MD1282') AND item_code = o.item_code GROUP BY item_code ),0) as stock_menge,
ifnull(( SELECT SUM(ifnull(warehouse_delivery_menge,0)) FROM product_tbl WHERE 1 = 1 AND del_yn = 'N' AND order_num = o.order_num AND item_code = o.item_code ),0) as warehouse_delivery_menge, ifnull(( SELECT SUM(ifnull(product_menge,0)) FROM product_tbl WHERE 1 = 1 AND del_yn = 'N' AND order_num = o.order_num AND item_code = o.item_code ),0) as product_menge, ( SELECT LEFT(product_po,12) FROM product_tbl WHERE 1 = 1 AND del_yn = 'N' AND order_num = o.order_num AND item_code = o.item_code LIMIT 1 ) as product_po, ( SELECT product_po FROM product_tbl WHERE 1 = 1 AND del_yn = 'N' AND order_num = o.order_num AND item_code = o.item_code LIMIT 1 ) as product_po_origin, o.creator, o.updater, o.date_created, o.date_updated FROM order_manager_tbl AS o
WHERE 1 = 1
AND o.order_num = #{order_num}
AND date_format(o.delivery_date, '%Y-%m-%d') BETWEEN left(#{delivery_date}, 10) AND right(#{delivery_date}, 10)
AND o.customer_nm = #{customer_nm}
AND o.item_code = #{item_code}
AND o.item_nm = #{item_nm}
ORDER BY o.order_num, o.date_created ASC

SELECT
business_code, order_num, item_code, item_nm, item_type_code, delivery_date, customer_code, customer_nm, request_num, sap_code, comment, land, manager_nm, unit_price, menge, total_price, success_status, order_stock_menge, stock_menge, product_po, creator, updater, date_created, date_updated
FROM
( SELECT o.business_code, o.order_num, o.item_code, o.item_nm, t.item_type_code, o.delivery_date, o.customer_code, o.customer_nm, o.request_num, o.sap_code, o.comment, o.land, o.manager_nm, o.unit_price, o.menge, o.total_price, o.success_status,
ifnull(( SELECT SUM(ifnull(delivery_order_menge,0)) FROM delivery_order_tbl WHERE del_yn = 'N' AND order_num = o.order_num AND item_code = o.item_code GROUP BY item_code ),0) as order_stock_menge,
ifnull(( SELECT SUM(ifnull(warehousing_qty,0)) FROM warehouse_tbl WHERE 1 = 1 AND (business_code = 'MD1243' OR business_code = 'MD1282') AND item_code = o.item_code GROUP BY item_code ),0) as stock_menge, ( SELECT LEFT(p.product_po,12) FROM product_tbl AS p LEFT JOIN product_operation_tbl AS po ON LEFT(p.product_po,12) = LEFT(po.product_po,12)
WHERE p.del_yn = 'N' AND po.del_yn = 'N'
AND p.order_num = o.order_num AND p.item_code = o.item_code LIMIT 1
) AS product_po, o.creator, o.updater, o.date_created, o.date_updated FROM order_manager_tbl AS o, material_tbl t WHERE o.item_code = t.item_code AND t.business_code != 'MD1244' AND t.del_yn = 'N' ) as pp
WHERE 1 = 1 AND business_code in ('MD1243', 'MD1282')
AND order_num = #{order_num}
AND date_format(delivery_date, '%Y-%m-%d') BETWEEN left(#{delivery_date}, 10) AND right(#{delivery_date}, 10)
AND manager_nm = #{manager_nm}
AND customer_nm = #{customer_nm}
AND request_num = #{request_num}
AND sap_code = #{sap_code}
AND comment = #{comment}
AND item_code = #{item_code}
AND item_nm = #{item_nm}
AND product_po IS NULL
AND product_po IS NOT NULL
AND business_code != 'MD1244' AND item_type_code IN ('MD1248','MD1249')
ORDER BY order_num, date_created ASC
