mapper.select:
SELECT d.business_code, d.delivery_num, d.delivery_order_menge, d.delivery_order_date, d.order_num, d.item_code, d.item_nm, ( SELECT location FROM material_tbl WHERE del_yn = 'N' AND item_code = d.item_code ) as location, ( SELECT request_num FROM order_manager_tbl WHERE 1 = 1 AND order_num = d.order_num AND item_code = d.item_code ) as request_num, d.delivery_req_date, d.customer_code, d.customer_nm, d.land, d.unit_price, d.menge, d.total_price, d.success_status,
( SELECT SUM( ifnull(warehousing_qty,0) ) FROM warehouse_tbl WHERE 1 = 1 AND (business_code = 'MD1243' OR business_code = 'MD1282') AND item_code = d.item_code GROUP BY item_code ) as stock_menge,
( SELECT SUM( ifnull(delivery_menge,0) ) FROM delivery_tbl WHERE 1 = 1 AND del_yn = 'N' AND delivery_num = d.delivery_num ) as delivery_menge, ( SELECT DATE_FORMAT(delivery_date, '%Y-%m-%d') FROM delivery_tbl WHERE 1 = 1 AND del_yn = 'N' AND delivery_num = d.delivery_num AND item_code = d.item_code
ORDER BY delivery_date desc LIMIT 1 ) as delivery_date, ( SELECT location FROM material_tbl WHERE del_yn = 'N' AND item_code = d.item_code LIMIT 1 ) as location,
IFNULL(( SELECT member_nm FROM sy_member_master WHERE member_id = d.creator ),'') as creator_nm,
creator, updater, date_created, date_updated FROM delivery_order_tbl as d
WHERE 1 = 1 AND del_yn = 'N'
AND ( SELECT request_num FROM order_manager_tbl WHERE 1 = 1 AND item_code = d.item_code AND order_num = d.order_num
) = #{request_num}
AND d.order_num = #{order_num}
AND date_format(d.delivery_order_date, '%Y-%m-%d') BETWEEN left(#{delivery_order_date}, 10) AND right(#{delivery_order_date}, 10)
AND date_format(d.delivery_req_date, '%Y-%m-%d') BETWEEN left(#{delivery_req_date}, 10) AND right(#{delivery_req_date}, 10)
AND d.customer_nm = #{customer_nm}
AND d.item_code = #{item_code}
AND d.item_nm = #{item_nm}
AND d.success_status = #{success_status}
ORDER BY d.order_num, d.delivery_order_date, d.item_code, d.date_created ASC

mapper.update:
UPDATE delivery_order_tbl SET del_yn = #{del_yn}
WHERE order_num = #{order_num},
item_code = #{item_code}, request_num = #{request_num}

UPDATE delivery_order_tbl 

	SET 

		success_status =  #{success_status},


		delivery_order_menge =  #{delivery_order_menge}

 WHERE delivery_num = #{delivery_num}

mapper.delete:
DELETE FROM delivery_order_tbl
WHERE 1 = 1

	AND delivery_num = #{delivery_num}


	AND item_code = #{item_code}

mapper.update:
UPDATE delivery_order_tbl 

SET del_yn = 'Y'

WHERE delivery_num = #{delivery_num}
