mapper.select:
SELECT batch_log_no , CASE WHEN batch_type = 'PR' THEN '생산' WHEN batch_type = 'HR' THEN '인사연동' WHEN batch_type = 'MA' THEN 'SAP마스터' WHEN batch_type = 'IF' THEN 'I/F정보' END AS batch_type , CASE WHEN batch_action = 'S' THEN '시스템' WHEN batch_action = 'U' THEN '사용자' END AS batch_action , date_format(batch_start_dt, '%Y.%m.%d %h:%i:%s') AS batch_start_dt , date_format(batch_end_dt, '%Y.%m.%d %h:%i:%s') AS batch_end_dt, batch_source_ds, batch_target_ds , batch_process_icnt, batch_process_ucnt , CASE WHEN batch_result = 'S' THEN '성공' ELSE '실패' END AS batch_result , batch_description , date_created, date_updated, creator, updater , if(updater = 'system', 'system', (SELECT member_nm FROM sy_member_master x WHERE x.member_id = a.updater)) as update_nm FROM sy_log_status a
AND batch_type = #{batch_type}
AND batch_action = #{batch_action}
AND batch_result = #{batch_result}
AND date_format(batch_start_dt, '%Y%m%d') BETWEEN #{batch_start_dt} AND #{batch_end_dt}
AND batch_source_ds LIKE CONCAT('%', #{batch_source_ds}, '%')
AND batch_target_ds LIKE CONCAT('%', #{batch_target_ds}, '%')
ORDER BY date_created ASC

mapper.insert:
INSERT INTO sy_log_status (
	batch_log_no, batch_type, batch_action, batch_start_dt, batch_end_dt, batch_source_ds, batch_target_ds
	, batch_process_icnt, batch_process_ucnt, batch_result, batch_description								
  , date_created, date_updated, creator, updater
) 
VALUES  (
	fn_common_seq('log_status'), #{batch_type}, #{batch_action}, #{batch_start_dt}, #{batch_end_dt}, #{batch_source_ds}, #{batch_target_ds}
	, #{batch_process_icnt}, #{batch_process_ucnt}, #{batch_result}, #{batch_description}
	, current_timestamp(), current_timestamp(), #{creator}, #{updater}
)
