mapper.select:
SELECT item_code, routing_code, routing_seq, routing_nm, routing_gubun, warehouse_gubun, outsourcing_gubun, machine_gubun, use_yn, creator, updater, date_created, date_updated FROM routing_master WHERE use_yn = 'Y'
AND item_code = #{item_code}
AND routing_code = #{routing_code}
ORDER BY routing_seq*1 , routing_code ASC

SELECT
product_po, product_class, product_status, product_status_info, routing_code, routing_nm , next_routing_code, next_routing_nm, product_req_date, product_date, order_num, item_code, item_nm, delivery_date, order_menge, warehouse_delivery_menge, product_menge, raw_out_yn, date_created, ( SELECT one_yn FROM material_tbl WHERE item_code = pp.item_code LIMIT 1 ) as one_yn FROM (
SELECT product_po, product_class, product_status, product_status_info, routing_code, ( SELECT GROUP_CONCAT(routing_nm, "(", routing_code, ")")
FROM product_operation_tbl WHERE del_yn = 'N' AND product_po = p.product_po AND routing_code = p.routing_code ) as routing_nm, IFNULL(( SELECT routing_code FROM product_operation_tbl WHERE del_yn = 'N' AND product_po = p.product_po AND real_seq*1 > ( SELECT real_seq FROM product_operation_tbl WHERE 1 = 1 AND del_yn = 'N' AND product_po = p.product_po AND routing_code = p.routing_code LIMIT 1 ) ORDER BY real_seq*1 asc LIMIT 1 ),'') as next_routing_code, IFNULL(( SELECT CONCAT(routing_nm, "(", routing_code, ")") FROM product_operation_tbl WHERE del_yn = 'N' AND product_po = p.product_po AND real_seq*1 > ( SELECT real_seq FROM product_operation_tbl WHERE 1 = 1 AND del_yn = 'N' AND product_po = p.product_po AND routing_code = p.routing_code LIMIT 1 ) ORDER BY real_seq*1 asc LIMIT 1 ),'') as next_routing_nm, product_req_date, product_date, order_num, item_code, item_nm, delivery_date, order_menge, warehouse_delivery_menge, product_menge, raw_out_yn, date_created
FROM product_tbl as p
WHERE 1 = 1 AND del_yn = 'N' ) AS pp
WHERE 1 = 1
AND order_num = #{order_num}
AND product_status = #{product_status}
AND product_status_info = #{product_status_info}
AND product_po = #{product_po}
AND date_format(product_req_date, '%Y%m%d') BETWEEN left(#{product_req_date}, 8) AND right(#{product_req_date}, 8)
AND routing_code = #{routing_code}
AND next_routing_code = #{next_routing_code}
AND product_class = #{product_class}
AND item_code = #{item_code}
AND item_nm = #{item_nm}
AND raw_out_yn = #{raw_out_yn}
AND product_po like 'P%'
AND product_po like 'S%'
ORDER BY SUBSTRING_INDEX(product_po,'_',1), SUBSTRING_INDEX(product_po,'_',-1)*1 , product_req_date, date_created ASC

SELECT p.product_po, p.product_status, p.product_status_info, po.status, po.routing_code , po.routing_nm, po.real_seq, p.product_req_date, product_date, p.order_num, p.item_code, p.item_nm, p.delivery_date, p.order_menge, p.warehouse_delivery_menge, p.product_menge, p.raw_out_yn
FROM product_tbl as p LEFT JOIN product_operation_tbl AS po ON p.product_po = po.product_po
WHERE p.del_yn = 'N' AND po.del_yn = 'N' AND po.real_seq = ( SELECT real_seq FROM product_operation_tbl AS po WHERE del_yn = 'N' AND product_po = p.product_po AND real_seq = ( SELECT real_seq FROM product_operation_tbl WHERE del_yn = 'N' AND product_po = po.product_po AND routing_code = 'V1' ) - 1 AND po.status = 'e' ) + 1
AND p.order_num = #{order_num}
AND p.product_status = #{product_status}
AND p.product_status_info = #{product_status_info}
AND po.status in ('i' , 'us', 'ue')
AND po.status in ('e' , 'y')
AND po.status = 'n'
AND p.product_po = #{product_po}
AND date_format(p.product_date, '%Y%m%d') BETWEEN left(#{product_date}, 8) AND right(#{product_date}, 8)
AND date_format(p.product_date, '%Y%m%d') BETWEEN left(#{product_req_date}, 8) AND right(#{product_req_date}, 8)
AND p.routing_code = #{p.routing_code}
AND p.product_class = #{p.product_class}
AND p.item_code = #{item_code}
AND p.item_nm = #{item_nm}
AND p.raw_out_yn = #{raw_out_yn}
ORDER BY SUBSTRING_INDEX(p.product_po,'_',1), SUBSTRING_INDEX(p.product_po,'_',-1)*1 , p.product_req_date, p.date_created ASC

mapper.update:
UPDATE product_tbl SET  product_req_date = #{product_req_date},
	updater = #{updater},	date_updated = current_timestamp()
 WHERE product_po = #{product_po}

mapper.select:
SELECT o.business_code, o.order_num, m.item_code, m.item_nm, m.item_type_code, o.delivery_date, o.customer_code, o.customer_nm, o.request_num, o.sap_code, o.comment, o.land, o.manager_nm, o.unit_price, SUM(IFNULL(o.menge,0)) as menge, o.total_price, o.success_status, m.safety_stock_yn as safety_stock_yn, IFNULL(m.safety_menge,0) as safety_stock_qty,
ifnull(( SELECT SUM(ifnull(warehousing_qty,0)) FROM warehouse_tbl WHERE 1 = 1 AND (business_code = 'MD1243' OR business_code = 'MD1282') AND item_code = o.item_code GROUP BY item_code ),0) as warehousing_qty, ( SELECT product_po FROM product_tbl AS p
WHERE p.del_yn = 'N'
AND p.order_num = o.order_num AND p.item_code = o.item_code LIMIT 1
) AS product_po, o.creator, o.updater, o.date_created, o.date_updated FROM material_tbl m LEFT JOIN order_manager_tbl AS o ON o.item_code = m.item_code WHERE 1 = 1 AND m.del_yn = 'N' AND m.item_type_code in ('MD1247','MD1248','MD1249')
AND o.business_code = #{business_code}
AND (o.business_code = #{business_code} OR o.business_code = #{business_codeT})
AND m.warehouse_code = #{warehouse_code}
AND m.item_code = #{item_code}
AND m.item_nm LIKE CONCAT('%', #{item_nm}, '%')
AND m.item_type_code = #{item_type_code}
AND m.safety_stock_yn = #{safety_stock_yn}
GROUP BY m.item_code
ORDER BY safety_stock_yn DESC , warehousing_qty*1 , (safety_stock_qty*1 - warehousing_qty*1) DESC

SELECT product_po, routing_code, routing_seq, real_seq, routing_nm, routing_gubun, status, machine_code, machine_nm, warehouse_gubun, member_emp_no, member_nm, prod_str_time, prod_end_time, CASE WHEN (prod_end_time IS NOT NULL) THEN CONCAT(SEC_TO_TIME(process_time - un_process_time),'') ELSE '' END AS process_time,
prod_menge, prod_fail_menge, ( SELECT prod_menge FROM product_operation_tbl WHERE del_yn = 'N' AND product_po = pp.product_po AND real_seq*1 < pp.real_seq ORDER BY real_seq*1 desc LIMIT 1 ) as prev_prod_menge, ( SELECT prod_fail_menge FROM product_operation_tbl WHERE del_yn = 'N' AND product_po = pp.product_po AND real_seq*1 < pp.real_seq ORDER BY real_seq*1 desc LIMIT 1 ) as prev_prod_fail_menge, outsourcing_gubun, machine_gubun, skip_yn, del_yn, creator, updater, date_created, date_updated FROM (
SELECT product_po, routing_code, routing_seq, real_seq, routing_nm, routing_gubun, status, machine_code, machine_nm, warehouse_gubun, member_emp_no, member_nm, date_format(prod_str_time,"%m-%d %H:%i") as prod_str_time, date_format(prod_end_time,"%m-%d %H:%i") as prod_end_time,
ifnull(TIME_TO_SEC( timediff( date_format(prod_end_time,"%Y-%m-%d %H:%i:%s"), date_format(prod_str_time,"%Y-%m-%d %H:%i:%s") )),0) AS process_time, IFNULL(( SELECT SUM(TIME_TO_SEC( timediff( date_format(unprod_end_time,"%Y-%m-%d %H:%i:%s"), date_format(unprod_str_time,"%Y-%m-%d %H:%i:%s") )) ) FROM product_unoperation_tbl WHERE del_yn = 'N' AND product_po = po.product_po AND routing_code = po.routing_code ),0) AS un_process_time,
ifnull(prod_menge,0) as prod_menge, ifnull(prod_fail_menge,0) as prod_fail_menge, outsourcing_gubun, machine_gubun,
skip_yn, del_yn, creator, updater, date_created, date_updated
FROM product_operation_tbl as po
WHERE 1 = 1 AND del_yn = 'N'
) AS pp WHERE 1 = 1
AND product_po = #{product_po}
AND routing_code = #{routing_code}
AND routing_seq = #{routing_seq}
AND member_nm = #{member_nm}
AND skip_yn = #{skip_yn}
AND outsourcing_gubun = #{outsourcing_gubun}
AND status = #{status}
ORDER BY product_po, real_seq*1 , date_created ASC

SELECT product_po, routing_code, routing_seq, real_seq, routing_nm, routing_gubun, status, machine_code, machine_nm, warehouse_gubun, member_emp_no, member_nm, prod_str_time, prod_end_time, CASE WHEN (prod_end_time IS NOT NULL) THEN CONCAT(SEC_TO_TIME(process_time - un_process_time),'') ELSE '' END AS process_time,
prod_menge, prod_fail_menge, ( SELECT prod_menge FROM product_operation_tbl WHERE del_yn = 'N' AND product_po = pp.product_po AND real_seq*1 < pp.real_seq ORDER BY real_seq*1 desc LIMIT 1 ) as prev_prod_menge, ( SELECT prod_fail_menge FROM product_operation_tbl WHERE del_yn = 'N' AND product_po = pp.product_po AND real_seq*1 < pp.real_seq ORDER BY real_seq*1 desc LIMIT 1 ) as prev_prod_fail_menge, outsourcing_gubun, machine_gubun, skip_yn, del_yn, creator, updater, date_created, date_updated FROM (
SELECT product_po, routing_code, routing_seq, real_seq, routing_nm, routing_gubun, status, machine_code, machine_nm, warehouse_gubun, member_emp_no, member_nm, date_format(prod_str_time,"%m-%d %H:%i") as prod_str_time, date_format(prod_end_time,"%m-%d %H:%i") as prod_end_time,
ifnull(TIME_TO_SEC( timediff( date_format(prod_end_time,"%Y-%m-%d %H:%i:%s"), date_format(prod_str_time,"%Y-%m-%d %H:%i:%s") )),0) AS process_time, IFNULL(( SELECT SUM(TIME_TO_SEC( timediff( date_format(unprod_end_time,"%Y-%m-%d %H:%i:%s"), date_format(unprod_str_time,"%Y-%m-%d %H:%i:%s") )) ) FROM product_unoperation_tbl WHERE del_yn = 'N' AND product_po = po.product_po AND routing_code = po.routing_code ),0) AS un_process_time,
ifnull(prod_menge,0) as prod_menge, ifnull(prod_fail_menge,0) as prod_fail_menge, outsourcing_gubun, machine_gubun,
skip_yn, del_yn, creator, updater, date_created, date_updated
FROM product_operation_tbl as po
WHERE 1 = 1 AND del_yn = 'N'
) AS pp WHERE 1 = 1
AND real_seq*1 >= ( SELECT real_seq FROM product_operation_tbl WHERE del_yn = 'N' AND product_po = pp.product_po AND routing_code = 'V1'
)
AND product_po = #{product_po}
AND routing_code = #{routing_code}
AND routing_seq = #{routing_seq}
AND member_nm = #{member_nm}
AND skip_yn = #{skip_yn}
AND outsourcing_gubun = #{outsourcing_gubun}
AND status = #{status}
ORDER BY product_po, real_seq*1 , date_created ASC

