mapper.select:
SELECT product_po, routing_code, ( SELECT routing_nm FROM product_operation_tbl WHERE routing_code = pu.routing_code AND product_po = pu.product_po GROUP BY routing_code ) as routing_nm,
unoperation_seq, type, member_emp_no, member_nm, date_format(unprod_str_time,"%m-%d %H:%i") as unprod_str_time, date_format(unprod_end_time,"%m-%d %H:%i") as unprod_end_time, time_format( timediff( date_format(unprod_end_time,"%Y-%m-%d %H:%i:%s"), date_format(unprod_str_time,"%Y-%m-%d %H:%i:%s") ), "%H:%i:%s") AS process_time, unprod_comment, del_yn, creator, updater, date_created, date_updated
FROM product_unoperation_tbl as pu WHERE 1 = 1 AND del_yn = 'N'
AND product_po = #{product_po}
AND routing_code = #{routing_code}
AND unoperation_seq = #{unoperation_seq}
AND member_nm = #{member_nm}, AND member_emp_no = ( SELECT member_emp_no FROM sy_member_master WHERE member_nm = #{member_nm} )
AND type != 'WH'
ORDER BY product_po, unoperation_seq, date_created ASC

SELECT po.product_po, po.routing_code, ( SELECT routing_nm FROM product_operation_tbl WHERE product_po = po.product_po AND routing_code = po.routing_code LIMIT 1 ) AS routing_nm,
pu.unoperation_seq, type, pu.member_emp_no, pu.member_nm, date_format(pu.unprod_str_time,"%m-%d %H:%i") as unprod_str_time, date_format(pu.unprod_end_time,"%m-%d %H:%i") as unprod_end_time, time_format( timediff( date_format(pu.unprod_end_time,"%Y-%m-%d %H:%i:%s"), date_format(pu.unprod_str_time,"%Y-%m-%d %H:%i:%s") ), "%H:%i:%s") AS process_time, pu.unprod_comment
FROM product_unoperation_tbl as pu LEFT JOIN product_operation_tbl AS po ON pu.product_po = po.product_po AND pu.routing_code = po.routing_code WHERE 1 = 1 AND po.del_yn = 'N' AND pu.del_yn = 'N' AND po.real_seq*1 >= ( SELECT real_seq FROM product_operation_tbl WHERE del_yn = 'N' AND product_po = po.product_po AND routing_code = 'V1'
)
AND pu.product_po = #{product_po}
AND pu.routing_code = #{routing_code}
AND pu.unoperation_seq = #{unoperation_seq}
AND pu.member_nm = #{member_nm}, AND pu.member_emp_no = ( SELECT member_emp_no FROM sy_member_master WHERE member_nm = #{member_nm} )
ORDER BY po.product_po, po.real_seq*1 ASC


SELECT MAX(unoperation_seq) 
FROM product_unoperation_tbl
WHERE del_yn = 'N'
AND product_po = #{product_po}
AND routing_code = #{routing_code}

