mapper.select:
SELECT
product_po, product_class, product_status, product_status_info, routing_code, routing_nm, machine_code, next_routing_nm, next_routing_code, product_req_date, product_date, order_num, item_code, item_nm, delivery_date, order_menge, warehouse_delivery_menge, product_menge, prod_fail_menge, raw_out_yn, routing_nms, routing_codes, prod_menges, prod_fail_menges
FROM ( SELECT product_po, product_class, product_status, product_status_info, routing_code, ( SELECT routing_nm FROM product_operation_tbl WHERE del_yn = 'N' AND product_po = p.product_po AND routing_code = p.routing_code ) as routing_nm, ( SELECT machine_code FROM product_operation_tbl WHERE del_yn = 'N' AND product_po = p.product_po AND routing_code = p.routing_code ) as machine_code, ( SELECT routing_nm FROM product_operation_tbl WHERE del_yn = 'N' AND product_po = p.product_po AND real_seq = ( SELECT (real_seq+1) AS next_real_seq FROM product_operation_tbl WHERE del_yn = 'N' AND product_po = p.product_po AND routing_code = p.routing_code ) ) as next_routing_nm, ( SELECT routing_code FROM product_operation_tbl WHERE del_yn = 'N' AND product_po = p.product_po AND real_seq = ( SELECT (real_seq+1) AS next_real_seq FROM product_operation_tbl WHERE del_yn = 'N' AND product_po = p.product_po AND routing_code = p.routing_code ) ) as next_routing_code, product_req_date, product_date, order_num, item_code, item_nm, delivery_date, order_menge, warehouse_delivery_menge, product_menge,
( SELECT prod_fail_menge FROM product_operation_tbl WHERE del_yn = 'N' AND product_po = p.product_po AND routing_code = p.routing_code
)as prod_fail_menge,
raw_out_yn, ( SELECT GROUP_CONCAT(routing_nm ORDER BY real_seq*1 ASC ) AS routing_nm FROM product_operation_tbl AS po WHERE del_yn = 'N' AND product_po = p.product_po
) AS routing_nms, ( SELECT
GROUP_CONCAT(routing_code ORDER BY real_seq*1 ASC ) AS routing_codes FROM product_operation_tbl AS po WHERE del_yn = 'N' AND product_po = p.product_po
) AS routing_codes, ( SELECT GROUP_CONCAT(ifnull(prod_menge,0) ORDER BY real_seq*1 ASC ) AS prod_menges FROM product_operation_tbl AS po WHERE del_yn = 'N' AND product_po = p.product_po
) AS prod_menges, ( SELECT GROUP_CONCAT(ifnull(prod_fail_menge,0) ORDER BY real_seq*1 ASC ) AS prod_fail_menges FROM product_operation_tbl AS po WHERE del_yn = 'N' AND product_po = p.product_po
) AS prod_fail_menges
FROM product_tbl as p
WHERE 1 = 1 AND del_yn = 'N'
) as t WHERE 1 = 1 AND product_status != 'y'
AND order_num = #{order_num}
AND product_status in ('n' , 'i') AND product_status_info in ('n' , 'e')
AND product_status = 'i' AND product_status_info in ('i')
AND product_status = 'i' AND product_status_info in ('e')
AND product_po = #{product_po}
AND date_format(product_req_date, '%Y%m%d') BETWEEN left(#{product_req_date}, 8) AND right(#{product_req_date}, 8)
AND routing_code = #{routing_code}
AND next_routing_nm = #{next_routing_nm}
AND product_class = #{product_class}
AND item_code = #{item_code}
AND item_nm = #{item_nm}
AND raw_out_yn = #{raw_out_yn}
ORDER BY item_code DESC
ORDER BY product_po DESC

SELECT
product_po, product_class, product_status, product_status_info, routing_code, routing_nm, machine_code, next_routing_nm, next_routing_code, product_req_date, product_date, order_num, item_code, item_nm, delivery_date, order_menge, warehouse_delivery_menge, product_menge, prod_fail_menge, raw_out_yn, ( SELECT GROUP_CONCAT(routing_nm ORDER BY real_seq*1 ASC ) AS routing_nm FROM product_operation_tbl AS po WHERE del_yn = 'N' AND product_po = t.product_po AND real_seq*1 >= t.real_seq*1
) AS routing_nms, ( SELECT
GROUP_CONCAT(routing_code ORDER BY real_seq*1 ASC ) AS routing_codes FROM product_operation_tbl AS po WHERE del_yn = 'N' AND product_po = t.product_po AND real_seq*1 >= t.real_seq*1
) AS routing_codes, ( SELECT GROUP_CONCAT(ifnull(prod_menge,0) ORDER BY real_seq*1 ASC ) AS prod_menges FROM product_operation_tbl AS po WHERE del_yn = 'N' AND product_po = t.product_po AND real_seq*1 >= t.real_seq*1
) AS prod_menges, ( SELECT GROUP_CONCAT(ifnull(prod_fail_menge,0) ORDER BY real_seq*1 ASC ) AS prod_fail_menges FROM product_operation_tbl AS po WHERE del_yn = 'N' AND product_po = t.product_po AND real_seq*1 >= t.real_seq*1
) AS prod_fail_menges
FROM ( SELECT product_po, product_class, product_status, product_status_info, ( SELECT real_seq FROM product_operation_tbl WHERE del_yn = 'N' AND product_po = p.product_po AND routing_code = p.routing_code ) AS real_seq, routing_code, ( SELECT routing_nm FROM product_operation_tbl WHERE del_yn = 'N' AND product_po = p.product_po AND routing_code = p.routing_code ) as routing_nm, ( SELECT machine_code FROM product_operation_tbl WHERE del_yn = 'N' AND product_po = p.product_po AND routing_code = p.routing_code ) as machine_code, ( SELECT routing_nm FROM product_operation_tbl WHERE del_yn = 'N' AND product_po = p.product_po AND real_seq = ( SELECT (real_seq+1) AS next_real_seq FROM product_operation_tbl WHERE del_yn = 'N' AND product_po = p.product_po AND routing_code = p.routing_code ) ) as next_routing_nm, ( SELECT routing_code FROM product_operation_tbl WHERE del_yn = 'N' AND product_po = p.product_po AND real_seq = ( SELECT (real_seq+1) AS next_real_seq FROM product_operation_tbl WHERE del_yn = 'N' AND product_po = p.product_po AND routing_code = p.routing_code ) ) as next_routing_code, product_req_date, product_date, order_num, item_code, item_nm, delivery_date, order_menge, warehouse_delivery_menge, product_menge,
( SELECT prod_fail_menge FROM product_operation_tbl WHERE del_yn = 'N' AND product_po = p.product_po AND routing_code = p.routing_code
)as prod_fail_menge,
raw_out_yn
FROM product_tbl as p
WHERE 1 = 1 AND del_yn = 'N' ) as t WHERE 1 = 1 AND product_status != 'y'
AND order_num = #{order_num}
AND product_status in ('n' , 'i') AND product_status_info in ('n' , 'e')
AND product_status = 'i' AND product_status_info in ('i')
AND product_status = 'i' AND product_status_info in ('e')
AND product_po = #{product_po}
AND date_format(product_req_date, '%Y%m%d') BETWEEN left(#{product_req_date}, 8) AND right(#{product_req_date}, 8)
AND routing_code = #{routing_code}
AND next_routing_nm = #{next_routing_nm}
AND product_class = #{product_class}
AND item_code = #{item_code}
AND item_nm = #{item_nm}
AND raw_out_yn = #{raw_out_yn}
ORDER BY item_code DESC
ORDER BY product_po DESC

SELECT product_po, item_code, item_nm,
CASE WHEN (outsourcing_price IS NULL) THEN IFNULL(( SELECT outsourcing_price FROM material_tbl WHERE del_yn = 'N' AND item_code = cc.item_code ),0) ELSE IFNULL(outsourcing_price,0) END AS outsourcing_price,
routing_code, routing_nm, prev_prod_menge, prod_menge, CASE WHEN (delivery_request_date IS NULL) THEN '' ELSE prev_prod_menge END AS req_prod_menge, account_code, ( SELECT account_nm FROM account_tbl WHERE account_code = cc.account_code LIMIT 1 ) as account_nm, delivery_request_date, ( SELECT warehouse_gubun FROM product_operation_tbl WHERE product_po = cc.product_po AND routing_code = cc.routing_code ) as warehouse_gubun,
date_format(delivery_request_date,'%Y-%m-%d') AS end_date
FROM (
SELECT vwtable2.product_po AS product_po, vwtable2.routing_code AS routing_code, vwtable2.routing_nm as routing_nm, vwtable2.routing_seq AS routing_seq, vwtable2.prod_str_time AS prod_str_time, vwtable2.prod_end_time AS prod_end_time , vwtable2.prod_menge AS prod_menge, d.product_po AS prev_product_po, d.routing_code AS prev_routing_code, d.routing_seq AS prev_routing_seq, d.prod_menge AS prev_prod_menge, vwtable2.delivery_request_date AS delivery_request_date, vwtable2.account_code as account_code, vwtable2.outsourcing_price as outsourcing_price, vwtable2.status as status, ( SELECT item_code FROM product_tbl WHERE del_yn = 'N' AND product_po = vwtable2.product_po ) AS item_code, ( SELECT item_nm FROM product_tbl WHERE del_yn = 'N' AND product_po = vwtable2.product_po ) AS item_nm FROM ( SELECT * FROM product_operation_tbl WHERE del_yn = 'N' AND outsourcing_gubun ='Y' ) AS vwtable2 LEFT JOIN product_operation_tbl d ON d.product_po = vwtable2.product_po AND d.prod_end_time IS not NULL AND d.real_seq = vwtable2.real_seq-1 AND d.status = 'e' UNION ALL SELECT vwtable2.product_po as product_po, vwtable2.routing_code as routing_code, vwtable2.routing_nm as routing_nm, vwtable2.routing_seq as routing_seq, vwtable2.prod_str_time as prod_str_time, vwtable2.prod_end_time AS prod_end_time , d.prod_menge AS prev_prod_menge, d.product_po AS prev_product_po , d.routing_code AS prev_routing_code , d.routing_seq AS prev_routing_seq , vwtable2.prod_menge as prod_menge, vwtable2.delivery_request_date AS delivery_request_date, vwtable2.account_code as account_code, vwtable2.outsourcing_price as outsourcing_price, vwtable2.status as status, vwtable2.item_code, vwtable2.item_nm FROM ( SELECT a.product_po, a.routing_code, a.routing_nm, a.routing_seq, a.prod_str_time, a.prod_end_time, CASE WHEN (b.product_class = 'f') THEN b.product_menge WHEN (b.product_class = 'o') THEN b.order_menge END AS prod_menge, a.delivery_request_date, a.account_code, a.outsourcing_price, a.status, b.item_code, b.item_nm FROM product_operation_tbl a, product_tbl b WHERE a.product_po =b.product_po AND a.outsourcing_gubun ='Y' AND real_seq = 1 AND status = 'n'
) AS vwtable2 LEFT JOIN product_operation_tbl d ON d.product_po = vwtable2.product_po AND d.real_seq = 1 AND d.status = 'n' ) cc
WHERE prev_routing_code IS NOT null
AND status != 'e' AND account_code IS NULL
AND product_po = #{product_po}
AND item_code = #{item_code}
AND item_nm = #{item_nm}
AND routing_nm = #{routing_nm}
AND account_code = ( SELECT account_code FROM account_tbl WHERE account_nm = #{account_nm} LIMIT 1 )
AND date_format(delivery_request_date, '%Y%m%d') BETWEEN left(#{delivery_request_date}, 8) AND right(#{delivery_request_date}, 8)
ORDER BY prod_end_time DESC

mapper.update:
UPDATE product_operation_tbl SET
account_code = #{account_code},
outsourcing_price = #{outsourcing_price},
delivery_request_date = #{delivery_request_date}, prod_str_time = current_timestamp(), updater = #{updater}, date_updated = current_timestamp()
WHERE product_po = #{product_po}
AND routing_code = #{routing_code}

mapper.select:
SELECT po.product_po, po.routing_code, po.routing_seq, po.routing_nm, po.routing_gubun, po.machine_code, po.machine_nm, po.prod_str_time, po.prod_end_time, IFNULL(( SELECT prod_menge FROM product_operation_tbl WHERE 1 = 1 AND product_po = p.product_po AND real_seq =
( SELECT real_seq FROM product_operation_tbl WHERE 1 = 1 AND product_po = p.product_po AND prod_menge IS NOT NULL ORDER BY real_seq DESC LIMIT 1 ) ),0) AS prod_menge, IFNULL(( SELECT prod_fail_menge FROM product_operation_tbl WHERE 1 = 1 AND product_po = p.product_po AND real_seq =
( SELECT real_seq FROM product_operation_tbl WHERE 1 = 1 AND product_po = p.product_po AND prod_fail_menge IS NOT NULL ORDER BY real_seq DESC LIMIT 1 ) ),0) AS prod_fail_menge, p.item_code, p.item_nm, p.product_date,
po.skip_yn, po.del_yn, po.creator, po.updater, po.date_created, po.date_updated
FROM product_operation_tbl as po LEFT JOIN product_tbl as p ON p.product_po = po.product_po WHERE 1 = 1 AND po.del_yn = 'N' AND p.del_yn = 'N'
AND po.product_po = #{product_po}
AND p.item_code = #{item_code}
AND p.item_nm = #{item_nm}
AND date_format(po.prod_end_time, '%Y%m%d') BETWEEN left(#{product_date}, 8) AND right(#{product_date}, 8)
AND LEFT(po.routing_code,1) = #{routing_code}
AND po.status = #{status}
GROUP BY p.product_po ORDER BY p.product_date DESC

mapper.update:
UPDATE product_operation_tbl
	SET
	status = #{status}

	WHERE 
	product_po = #{product_po}
	AND routing_code = #{routing_code}

UPDATE product_tbl
	SET
	product_status_info = #{product_status_info}

	WHERE 
	product_po = #{product_po}

mapper.select:

SELECT order_num, item_code, item_nm, product_status_info
FROM product_tbl
WHERE del_yn = 'N'


	AND order_num = #{order_num}


	AND item_code = #{item_code}


ORDER BY date_updated DESC

mapper.update:
UPDATE product_operation_tbl
SET

		outsourcing_gubun = #{outsourcing_gubun}

	, date_updated = CURRENT_TIMESTAMP()
	, updater = #{updater}

WHERE

	routing_code = #{routing_code}


	AND product_po = #{product_po}
